import React from 'react';
import '../../styles/CartItem.css';

const CartItem = ({ item }) => {
  return (
    <div className="cart-item">
      <h4>{item.name}</h4>
      <p>Quantity: {item.quantity}</p>
      <p>Price: ${item.price.toFixed(2)}</p>
    </div>
  );
};

export default CartItem;
