import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Home from './components/Home';
import Login from './components/Login';
import Register from './components/Register';
import Products from './components/Products';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    // Simulate successful login (e.g., after authentication)
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  return (
    <Router>
      <Routes>
        {/* Route to Home */}
        <Route path="/" element={<Home />} />

        {/* Route to Login */}
        {!isLoggedIn && <Route path="/login" element={<Login onLogin={handleLogin} />} />}

        {/* Route to Register */}
        {!isLoggedIn && <Route path="/register" element={<Register />} />}

        {/* Protected Route to Products */}
        {isLoggedIn ? (
          <Route path="/products" element={<Products />} />
        ) : (
          <Route path="/products" element={<Navigate to="/login" />} />
        )}

        {/* Redirect to Home for unknown paths */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
