import React from 'react';
import '../styles/CartItem.css';

const CartItem = ({ item }) => {
  const { id, name, price } = item;

  return (
    <div className="cart-item">
      <h4>{name}</h4>
      <p>Price: ${price}</p>
    </div>
  );
};

export default CartItem;
